mapper.insert:
	/** insertImgScrap **/
	INSERT INTO TB_IMG_SCRAP_INFO
	     ( SCRAP_NO
	     , TITLE
	     , CONT
	     , ORG_IMG_URL
	     , ORG_IMG_FILE_NAME
	     , IMG_SAVE_PATH
	     , IMG_SAVE_FILE_NAME
	     , THUMBNAIL_SAVE_PATH
	     , THUMBNAIL_FILE_NAME
	     , IMG_FILE_EXT
	     , IMG_SERVICE_URL
	     , HITS
	     , DEL_YN
	     , INSERT_USER_NO
	     , INSERT_DT
	     , UPDATE_USER_NO
	     , UPDATE_DT
	     ) VALUES ( 
	       SCRAP_NO_SEQ.NEXTVAL
	     , #{title}
	     , #{cont}
	     , #{orgImgUrl}
	     , #{orgImgFileName}
	     , #{imgSavePath}
	     , #{imgSaveFileName}
	     , #{thumbnailSavePath}
	     , #{thumbnailFileName}
	     , #{imgFileExt}
	     , #{imgServiceUrl}
	     , 0
	     , 'N'
	     , #{insertUserNo}
	     , NOW()
	     , #{updateUserNo}
	     , NOW()
	     )

mapper.update:
/** modifyImgScrap **/
UPDATE TB_IMG_SCRAP_INFO
   SET TITLE = #{title}
     , CONT = #{cont}
     , UPDATE_USER_NO = #{userNo}
     , UPDATE_DT = NOW()
 WHERE SCRAP_NO = #{scrapNo}

/** modifyImgScrapHits **/
UPDATE TB_IMG_SCRAP_INFO
   SET HITS = HITS + 1
 WHERE SCRAP_NO = #{scrapNo}

/** removeImgScrap **/
UPDATE TB_IMG_SCRAP_INFO
   SET DEL_YN = #{delYn}
     , UPDATE_USER_NO = #{userNo}
     , UPDATE_DT = NOW()
 WHERE SCRAP_NO = #{scrapNo}
